import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';

function Details() {
  const { id } = useParams();
  const [formData, setFormData] = useState({});
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const res = await axios.get(`/api/user1/${id}`);
        setFormData(res.data);
        setIsLoading(false);
      } catch (err) {
        console.error(err.response.data);
      }
    };

    fetchUserDetails();
  }, [id]);

  if (isLoading) {
    return <p>Loading user details...</p>;
  }

  return (
    <div className='container mi-3'>
      <Link to="/List" type="button" className="btn btn-secondary mt-5">
        Back
      </Link>

      <div className='container'>
        <h3>Name: {formData.name}</h3>
        <h3>Age: {formData.age}</h3>
        <h3>Email: {formData.email}</h3>
        <h3>Mobile: {formData.mobile}</h3>
        <h3>Work: {formData.work}</h3>
        <h3>Address: {formData.add}</h3>
        <h3>Description: {formData.desc}</h3>
      </div>
    </div>
  );
}

export default Details;
