import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';

function Details() {
  const { _id } = useParams("");
  console.log(_id);
  const [formData, setFormData] = useState({});
  console.log(setFormData);
  
  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const config = {
          headers: {
            'Content-Type': 'application/json'
          }
        };
        const res = await axios.get(`/api/user1/${_id},`, config );
        setFormData(res.data);
        console.log(res.data);
      } catch (err) {
        console.error(err.response.data);
      }
    };

    fetchUserDetails();
  }, [_id]);

  return (
    <div className='container mi-3'>
      <Link to="/List" type="button" className="btn btn-secondary mt-5">
        Back
      </Link>

      {formData ? (
        <div className='container'>
          <h3>Name: {formData.name}</h3>
          <h3>Age: {formData.age}</h3>
          <h3>Email: {formData.email}</h3>
          <h3>Mobile: {formData.mobile}</h3>
          <h3>Work: {formData.work}</h3>
          <h3>Address: {formData.add}</h3>
          <h3>Description: {formData.desc}</h3>
        </div>
      ) : (
        <p>Loading user details...</p>
      )}
    </div>
  );
}

export default Details;
